import React, { useEffect } from 'react';
import { useGameLogic } from './hooks/useGameLogic';
import Board from './components/Board';
import './App.css';

function App() {
  const {
    board,
    currentPiece,
    score,
    level,
    lines,
    gameOver,
    isPaused,
    movePiece,
    rotatePiece,
    hardDrop,
    startGame,
    togglePause
  } = useGameLogic();

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (gameOver) return;

    switch (e.key) {
      case 'ArrowLeft':
        movePiece('left');
        break;
      case 'ArrowRight':
        movePiece('right');
        break;
      case 'ArrowDown':
        movePiece('down');
        break;
      case 'ArrowUp':
        rotatePiece();
        break;
      case ' ':
        hardDrop();
        break;
      case 'p':
      case 'P':
        togglePause();
        break;
    }
  };

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (gameOver) return;

      switch (e.key) {
        case 'ArrowLeft':
          movePiece('left');
          break;
        case 'ArrowRight':
          movePiece('right');
          break;
        case 'ArrowDown':
          movePiece('down');
          break;
        case 'ArrowUp':
          rotatePiece();
          break;
        case ' ':
          hardDrop();
          break;
        case 'p':
        case 'P':
          togglePause();
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [movePiece, rotatePiece, hardDrop, togglePause, gameOver]);

  return (
    <div className="App">
      <div className="game-container">
        <div className="game-info">
          <h1>Tetris</h1>
          <div className="score">Score: {score}</div>
          <div className="level">Level: {level}</div>
          <div className="lines">Lines: {lines}</div>
          <div className="controls">
            <button 
              onClick={startGame}
              className="control-button"
            >
              {gameOver ? 'New Game' : 'Restart'}
            </button>
            <button 
              onClick={togglePause}
              className="control-button"
              disabled={gameOver}
            >
              {isPaused ? 'Resume' : 'Pause'}
            </button>
          </div>
          <div className="instructions">
            <h3>Controls:</h3>
            <p>← → : Move</p>
            <p>↑ : Rotate</p>
            <p>↓ : Soft Drop</p>
            <p>Space : Hard Drop</p>
            <p>P : Pause/Resume</p>
          </div>
        </div>
        <div 
          className="game-board"
          tabIndex={0}
          onKeyDown={handleKeyDown}
        >
          <Board 
            board={board} 
            currentPiece={currentPiece} 
            gameOver={gameOver}
            isPaused={isPaused}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
